package JSONImages;

import java.awt.BorderLayout;
import java.util.*;
import javax.swing.*;
import java.awt.event.*;
import java.net.MalformedURLException;
import java.net.URL;

public class PanMain extends JPanel {
    
    //LoadImages into labels
    
    JLabel LblImage;
    ImageIcon imgIc;
    //URL
    URL url;
    
    public PanMain() throws MalformedURLException{
        url = new URL("data:image/jpeg;base64,");
        LblImage = new JLabel("IMAGE GOES HERE!!!");
        imgIc = new ImageIcon(url);
        LblImage.setIcon(imgIc);
        add(BorderLayout.CENTER,LblImage);
        
        
    }
    
}
